// --- CONSTANTS ---
const SHEET_ID = "10fogMQiQt4aJ4CQtC-DgAhGOXQCu32LaSMDoXQf959w";
const FOLDER_ID = "1Pa0efq5zuayPboDZN0-re4-P0rzaxsqQ"; // PDF Folder

const DATA_TAB = "Audit Record";
const VALIDATION_TAB = "Data Validation";
const QUESTIONS_TAB = "Audit Questions";
const ACTIONS_TAB = "Corrective Actions";
const EMAIL_TAB = "Email";

// --- Robust Header Finder ---
function findHeaderIndex(headers, name) {
  const target = name.replace(/[\s_]+/g, '').toUpperCase();
  for (let i = 0; i < headers.length; i++) {
    const current = String(headers[i]).replace(/[\s_]+/g, '').toUpperCase();
    if (current === target) {
      return i;
    }
  }
  return -1;
}

/**
 * Main function: Serves the HTML file as a web page.
 */
function doGet(e) {
  const template = HtmlService.createTemplateFromFile('Index');
  template.loadAsReviewer = (e.parameter.mode === 'review');
  return template.evaluate().setTitle('Quarterly Site Safety Audit');
}

/**
 * Gets the list of email addresses from the "Email" tab.
 */
function getEmailList() {
  try {
    const sheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(EMAIL_TAB);
    if (!sheet) {
      console.warn(`Could not find "${EMAIL_TAB}" sheet. No emails will be sent.`);
      return null;
    }
    const lastRow = sheet.getLastRow();
    if (lastRow < 2) {
      console.warn(`No emails found in "${EMAIL_TAB}" sheet.`);
      return null;
    }
    const emailData = sheet.getRange("A2:A" + lastRow).getValues();
    const emailList = emailData.flat().filter(String).map(s => s.trim());

    if (emailList.length === 0) return null;
    return emailList.join(',');

  } catch (e) {
    Logger.log(`Error in getEmailList: ${e.message}`);
    return null;
  }
}

/**
 * Server-side function to get ALL data needed to build the page.
 */
function getPageData() {
  const sheet = SpreadsheetApp.openById(SHEET_ID);
  let auditors = [], sites = [], sections = {};

  try {
    // 1. Get Dropdown Data
    const validationSheet = sheet.getSheetByName(VALIDATION_TAB);
    if (validationSheet) {
      const lastRow = validationSheet.getLastRow();
      if (lastRow > 1) {
        auditors = validationSheet.getRange("B2:B" + lastRow).getValues().flat().filter(String).map(s => s.trim()); 
        sites = validationSheet.getRange("A2:A" + lastRow).getValues().flat().filter(String).map(s => s.trim());
      }
    } else {
      throw new Error(`Sheet tab "${VALIDATION_TAB}" not found.`);
    }

    // 2. Get Audit Questions
    const questionsSheet = sheet.getSheetByName(QUESTIONS_TAB);
    if (questionsSheet) {
      const lastRow = questionsSheet.getLastRow();
      if (lastRow > 1) {
        const questionDataWithHeaders = questionsSheet.getRange("A1:E" + lastRow).getValues();
        const headers = questionDataWithHeaders[0];

        const sectionIndex = findHeaderIndex(headers, "Section");
        const questionIndex = findHeaderIndex(headers, "Question");
        const questionIdIndex = findHeaderIndex(headers, "Question_ID");
        const possibleIndex = findHeaderIndex(headers, "Possible"); 
        const scoreIndex = findHeaderIndex(headers, "Score");

        if ([sectionIndex, questionIndex, questionIdIndex, possibleIndex, scoreIndex].includes(-1)) {
          let missing = [];
          if (sectionIndex === -1) missing.push("Section");
          if (questionIndex === -1) missing.push("Question");
          if (questionIdIndex === -1) missing.push("Question_ID");
          if (possibleIndex === -1) missing.push("Possible");
          if (scoreIndex === -1) missing.push("Score");
          throw new Error(`CRITICAL: Could not find column(s): [${missing.join(", ")}] in your '${QUESTIONS_TAB}' sheet. Please check spelling.`);
        }

        const questionData = questionDataWithHeaders.slice(1);

        questionData.forEach(row => {
          const sectionName = String(row[sectionIndex]).trim();
          const questionText = String(row[questionIndex]).trim();
          const questionId = String(row[questionIdIndex]).trim();
          const defaultPossible = row[possibleIndex];
          const defaultScore = row[scoreIndex];

          if (sectionName && !sections[sectionName]) {
            sections[sectionName] = [];
          }
          if(sectionName && questionId) {
            sections[sectionName].push({
              question: questionText,
              questionId: questionId,
              score: defaultScore,
              possible: defaultPossible
            });
          }
        });
      }
    } else {
       throw new Error(`Sheet tab "${QUESTIONS_TAB}" not found.`);
    }

    return {
      auditors: auditors,
      sites: sites,
      sections: sections
    };

  } catch (e) {
    Logger.log(e);
    return { error: e.message.toString() };
  }
}

/**
 * Searches the "Audit Record" tab for a matching audit.
 */
function getExistingAudit(site, quarter, fiscalYear) {
  try {
    const sheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(DATA_TAB);
    if (!sheet) throw new Error(`Sheet tab "${DATA_TAB}" not found.`);

    const data = sheet.getDataRange().getValues();

    const rawId = `${site}_${quarter}_FY${fiscalYear}`;
    const auditIdToFind = rawId.replace(/[\s_]+/g, '').toUpperCase();

    const headers = data[0];

    const auditIdIndex = findHeaderIndex(headers, "Audit ID");
    const sectionIndex = findHeaderIndex(headers, "Category");
    const questionIndex = findHeaderIndex(headers, "Question Written");
    const scoreIndex = findHeaderIndex(headers, "Score");
    const possibleIndex = findHeaderIndex(headers, "Possible Score");
    const commentsIndex = findHeaderIndex(headers, "Comments");
    const questionIdIndex = findHeaderIndex(headers, "Question_ID");

    if (auditIdIndex === -1) return { status: "error", message: `CRITICAL: Could not find 'Audit ID' column in '${DATA_TAB}' sheet.` };
    if (sectionIndex === -1) return { status: "error", message: `CRITICAL: Could not find 'Category' column in '${DATA_TAB}' sheet.` };
    if (questionIndex === -1) return { status: "error", message: `CRITICAL: Could not find 'Question Written' column in '${DATA_TAB}' sheet.` };
    if (possibleIndex === -1) return { status: "error", message: `CRITICAL: Could not find 'Possible Score' column in '${DATA_TAB}' sheet.` };
    if (questionIdIndex === -1) return { status: "error", message: `CRITICAL: Could not find 'Question_ID' column in '${DATA_TAB}' sheet.` };

    const matchingRows = data.filter((row, index) => {
      if (index === 0) return false;
      const sheetId = String(row[auditIdIndex]);
      const standardizedSheetId = sheetId.replace(/[\s_]+/g, '').toUpperCase();
      return standardizedSheetId === auditIdToFind;
    });

    if (matchingRows.length === 0) {
      return { 
        status: "not_found", 
        debug_id: `App searched for: [${auditIdToFind}]` 
      };
    }

    const auditData = matchingRows.map(row => {
      const stdSection = String(row[sectionIndex]).replace(/\s+/g, ' ').trim();
      const stdQuestion = String(row[questionIndex]).replace(/\s+/g, ' ').trim();

      return {
        section: stdSection,
        question: stdQuestion,
        score: row[scoreIndex],
        possible: row[possibleIndex],
        comments: (commentsIndex !== -1) ? row[commentsIndex] : "",
        questionId: String(row[questionIdIndex]).trim()
      };
    });

    return auditData;

  } catch (e) {
    Logger.log(e);
    return { status: "error", message: e.message.toString() };
  }
}


/**
 * Processes the submitted form data and saves it to the "Audit Record" sheet.
 */
function processForm(formData) {
  try {
    const sheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(DATA_TAB);
    if (!sheet) throw new Error(`Sheet tab "${DATA_TAB}" not found.`);

    const allData = sheet.getDataRange().getValues();
    const headers = allData[0];

    const auditIdIndex = findHeaderIndex(headers, "Audit ID");
    const siteIndex = findHeaderIndex(headers, "Site");
    const auditorIndex = findHeaderIndex(headers, "Auditor");
    const dateIndex = findHeaderIndex(headers, "Date");
    const quarterIndex = findHeaderIndex(headers, "Quarter");
    const fiscalYearIndex = findHeaderIndex(headers, "Fiscal Year");
    const sectionIndex = findHeaderIndex(headers, "Category");
    const questionIndex = findHeaderIndex(headers, "Question Written");
    const scoreIndex = findHeaderIndex(headers, "Score");
    const possibleIndex = findHeaderIndex(headers, "Possible Score");
    const commentsIndex = findHeaderIndex(headers, "Comments");
    const auditRowIdIndex = findHeaderIndex(headers, "Audit_Row_ID");
    const questionIdIndex = findHeaderIndex(headers, "Question_ID");
    const photoUrlIndex = findHeaderIndex(headers, "Section_Photo_URL");

    if (auditIdIndex === -1) throw new Error(`Critical column 'Audit ID' is missing from '${DATA_TAB}'.`);
    if (questionIdIndex === -1) throw new Error(`Critical column 'Question_ID' is missing from '${DATA_TAB}'.`);
    if (possibleIndex === -1) throw new Error(`Critical column 'Possible Score' is missing from '${DATA_TAB}'.`);
    if (auditRowIdIndex === -1) throw new Error(`Critical column 'Audit_Row_ID' is missing from '${DATA_TAB}'. Please add this column.`);
    if (photoUrlIndex === -1) throw new Error(`Critical column 'Section_Photo_URL' is missing from '${DATA_TAB}'. Please add this column.`);

    const fiscalYear = calculateFiscalYear(formData.header.auditDate);
    const auditId = `${formData.header.site}_${formData.header.quarter}_FY${fiscalYear}`.replace(/[\s_]+/g, '').toUpperCase();

    // 1. Handle file uploads and create a map of photo URLs
    const photoUrlMap = {};
    if (formData.photos) {
      for (const sectionName in formData.photos) {
        const fileData = formData.photos[sectionName];
        if (fileData) {
          const fileName = `${auditId}_${sectionName.replace(/\s+/g, '_')}`;
          const fileUrl = saveFile(fileData, fileName);
          photoUrlMap[sectionName] = fileUrl;
        }
      }
    }

    // 2. Build the new rows to add
    const rowsToAdd = [];
    const commentedRowData = [];

    for (const sectionName in formData.sections) {
      const questions = formData.sections[sectionName];
      const sectionPhotoUrl = photoUrlMap[sectionName] || "";

      for (const q of questions) {
        let newRow = new Array(headers.length);

        newRow[siteIndex] = formData.header.site;
        newRow[auditorIndex] = formData.header.auditor;
        newRow[dateIndex] = new Date(formData.header.auditDate);
        newRow[quarterIndex] = formData.header.quarter;
        newRow[fiscalYearIndex] = fiscalYear;
        newRow[sectionIndex] = sectionName;
        newRow[questionIndex] = q.question;
        newRow[scoreIndex] = q.score;
        newRow[possibleIndex] = q.possible;
        if (commentsIndex !== -1) newRow[commentsIndex] = q.comments;
        newRow[auditIdIndex] = auditId;
        newRow[questionIdIndex] = q.questionId;
        newRow[photoUrlIndex] = sectionPhotoUrl;

        const auditRowId = `${formData.header.site}${formData.header.auditor}${new Date(formData.header.auditDate).toLocaleDateString()}${sectionName}Q${q.questionId}`;
        newRow[auditRowIdIndex] = auditRowId.replace(/[\s\/]+/g, '');

        rowsToAdd.push(newRow);

        if (q.isCorrectiveAction) {
          commentedRowData.push({
            auditRowId: newRow[auditRowIdIndex],
            site: newRow[siteIndex],
            section: newRow[sectionIndex],
            question: newRow[questionIndex],
            comment: newRow[commentsIndex]
          });
        }
      }
    }

    // 3. Filter out the old data
    const newData = [headers];
    for (let i = 1; i < allData.length; i++) {
      const row = allData[i];
      const sheetId = String(row[auditIdIndex]).replace(/[\s_]+/g, '').toUpperCase();
      if (sheetId !== auditId) {
        newData.push(row);
      }
    }

    // 4. Combine and write back
    const finalData = newData.concat(rowsToAdd);
    sheet.clear();
    sheet.getRange(1, 1, finalData.length, headers.length).setValues(finalData);

    // 5. Create PDF
    const sectionsToPrint = formData.checkedSectionNames || Object.keys(formData.sections);
    const pdfResponse = createPdfReport(formData, fiscalYear, auditId, sectionsToPrint, photoUrlMap);

    // 6. Update Corrective Actions
    updateCorrectiveActions(commentedRowData);

    // 7. Send Email Notification
    const mainEmailList = getEmailList();
    const adHocEmails = formData.header.adHocEmails || "";
    const combinedEmailList = [mainEmailList, adHocEmails].filter(String).join(',');

    // --- THIS IS THE FIX (Part 1/3) ---
    // We pass the pdfResponse.blob instead of pdfResponse.file
    if (combinedEmailList && pdfResponse.blob) {
      sendAuditNotification(combinedEmailList, pdfResponse.blob, formData, fiscalYear);
    }
    // --- END FIX ---

    return { 
      status: "success", 
      message: `Audit for ${auditId} saved.`,
      pdfUrl: pdfResponse.url,
      pdfName: pdfResponse.name
    };

  } catch (e) {
    Logger.log(e);
    return { status: "error", message: e.message.toString() };
  }
}

/**
 * Helper function to save a Base64 file to Drive.
 */
function saveFile(fileData, newFileName) {
  try {
    const folder = DriveApp.getFolderById(FOLDER_ID);
    const base64Data = fileData.base64.split(',')[1];
    const fileBlob = Utilities.newBlob(Utilities.base64Decode(base64Data), fileData.mimeType, newFileName);
    const file = folder.createFile(fileBlob);
    return file.getUrl();
  } catch (e) {
    Logger.log(`Error in saveFile: ${e.message}`);
    return `Error saving file: ${e.message}`;
  }
}

/**
 * Checks for and adds new corrective actions.
 */
function updateCorrectiveActions(commentedRowData) {
  // ... (This function is unchanged)
  try {
    const caSheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(ACTIONS_TAB);
    if (!caSheet) throw new Error(`Sheet tab "${ACTIONS_TAB}" not found.`);

    const caHeaders = caSheet.getRange(1, 1, 1, caSheet.getLastColumn()).getValues()[0];
    const caAuditRowIdIndex = findHeaderIndex(caHeaders, "Audit_Row_ID");
    const caSiteIndex = findHeaderIndex(caHeaders, "Site");
    const caSectionIndex = findHeaderIndex(caHeaders, "Section");
    const caQuestionIndex = findHeaderIndex(caHeaders, "Question");
    const caAuditorCommentIndex = findHeaderIndex(caHeaders, "Auditor_Comment");
    const caStatusIndex = findHeaderIndex(caHeaders, "Status");
    const caLastUpdatedIndex = findHeaderIndex(caHeaders, "Last_Updated");

    const existingCaIds = new Set();
    if (caSheet.getLastRow() > 1) {
      const existingIds = caSheet.getRange(2, caAuditRowIdIndex + 1, caSheet.getLastRow() - 1, 1).getValues();
      existingIds.forEach(id => existingCaIds.add(id[0]));
    }

    const newCaRows = [];

    commentedRowData.forEach(row => {
      if (row.comment && !existingCaIds.has(row.auditRowId)) {
        let newRow = new Array(caHeaders.length);
        newRow[caAuditRowIdIndex] = row.auditRowId;
        newRow[caSiteIndex] = row.site;
        newRow[caSectionIndex] = row.section;
        newRow[caQuestionIndex] = row.question;
        newRow[caAuditorCommentIndex] = row.comment;
        newRow[caStatusIndex] = "Open";
        newRow[caLastUpdatedIndex] = new Date();

        newCaRows.push(newRow);
      }
    });

    if (newCaRows.length > 0) {
      caSheet.getRange(caSheet.getLastRow() + 1, 1, newCaRows.length, caHeaders.length).setValues(newCaRows);
    }

  } catch (e) {
    Logger.log(`Error in updateCorrectiveActions: ${e.message}`);
  }
}

/**
 * --- THIS IS THE FIX (Part 2/3) ---
 * Creates the PDF report and saves it to Google Drive.
 * Now returns the raw blob as well as the file.
 */
function createPdfReport(formData, fiscalYear, auditId, sectionsToPrint, photoUrlMap) {
  try {
    const folder = DriveApp.getFolderById(FOLDER_ID);
    const header = formData.header;

    const fileName = `${header.auditor} - ${header.site} - ${header.quarter} - FY${fiscalYear}`;

    const template = HtmlService.createTemplateFromFile('Report');
    template.header = header;
    template.sections = formData.sections;
    template.sectionsToPrint = sectionsToPrint;
    template.fiscalYear = fiscalYear;
    template.photoUrls = photoUrlMap || {};

    const html = template.evaluate().getContent();
    const pdfBlob = HtmlService.createHtmlOutput(html)
                              .getBlob()
                              .getAs('application/pdf')
                              .setName(fileName);

    const pdfFile = folder.createFile(pdfBlob);

    // Return the blob *and* the file/url
    return { url: pdfFile.getUrl(), name: pdfFile.getName(), file: pdfFile, blob: pdfBlob };
  } catch (e) {
    Logger.log(e);
    return { url: null, name: `Error creating PDF: ${e.message}`, file: null, blob: null };
  }
}

/**
 * --- THIS IS THE FIX (Part 3/3) ---
 * Now accepts a pdfBlob directly.
 */
function sendAuditNotification(combinedEmailList, pdfBlob, formData, fiscalYear) {
  try {
    const header = formData.header;
    const subject = `New Audit Submitted: ${header.site} - ${header.quarter} FY${fiscalYear}`;
    const body = `
      A new quarterly audit has been submitted by ${header.auditor}.

      Site: ${header.site}
      Quarter: ${header.quarter} (FY${fiscalYear})
      Date: ${new Date(header.auditDate).toLocaleDateString()}
      Score: ${header.totalPercentage}

      The full PDF report is attached.
    `;

    GmailApp.sendEmail(combinedEmailList, subject, body, {
      attachments: [pdfBlob], // Use the blob directly
      name: "Automated Audit System"
    });

  } catch (e) {
    Logger.log(`Error in sendAuditNotification: ${e.message}`);
  }
}

/**
 * Helper function to calculate fiscal year.
 */
function calculateFiscalYear(dateString) {
  // ... (This function is unchanged)
  const date = new Date(dateString);
  const month = date.getMonth();
  const year = date.getFullYear();
  if (month >= 8) {
    return year + 1;
  } else {
    return year;
  }
}

/**
 * Gets all items with "Pending Review" status for all sites.
 */
function getPendingActions() {
  // ... (This function is unchanged)
  try {
    const sheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(ACTIONS_TAB);
    if (!sheet) throw new Error(`Sheet tab "${ACTIONS_TAB}" not found.`);

    const data = sheet.getDataRange().getValues();
    const headers = data[0];

    const idIndex = findHeaderIndex(headers, "Audit_Row_ID");
    const siteIndex = findHeaderIndex(headers, "Site");
    const sectionIndex = findHeaderIndex(headers, "Section");
    const questionIndex = findHeaderIndex(headers, "Question");
    const commentIndex = findHeaderIndex(headers, "Auditor_Comment");
    const responseIndex = findHeaderIndex(headers, "Site_Response");
    const personIndex = findHeaderIndex(headers, "Person_Responsible");
    const dateIndex = findHeaderIndex(headers, "Target_Date");
    const statusIndex = findHeaderIndex(headers, "Status");

    if ([idIndex, siteIndex, sectionIndex, questionIndex, commentIndex, responseIndex, personIndex, dateIndex, statusIndex].includes(-1)) {
      let missing = [];
      if (idIndex === -1) missing.push("Audit_Row_ID");
      if (siteIndex === -1) missing.push("Site");
      if (sectionIndex === -1) missing.push("Section");
      if (questionIndex === -1) missing.push("Question");
      if (commentIndex === -1) missing.push("Auditor_Comment");
      if (responseIndex === -1) missing.push("Site_Response");
      if (personIndex === -1) missing.push("Person_Responsible");
      if (dateIndex === -1) missing.push("Target_Date");
      if (statusIndex === -1) missing.push("Status");
      throw new Error(`CRITICAL: Could not find column(s): [${missing.join(", ")}] in your '${ACTIONS_TAB}' sheet. Please check spelling.`);
    }

    const actions = [];

    for (let i = 1; i < data.length; i++) {
      const row = data[i];
      const rowStatus = String(row[statusIndex]).trim().toUpperCase();

      if (rowStatus === "PENDING REVIEW") {
        actions.push({
          auditRowId: row[idIndex],
          site: row[siteIndex],
          section: row[sectionIndex],
          question: row[questionIndex],
          auditorComment: row[commentIndex],
          siteResponse: row[responseIndex] || "",
          person: row[personIndex] || "",
          targetDate: row[dateIndex] ? new Date(row[dateIndex]).toLocaleDateString('en-CA') : "",
          status: rowStatus
        });
      }
    }
    return actions;

  } catch (e) {
    Logger.log(e);
    return { error: e.message.toString() };
  }
}

/**
 * Approves or Denies a submitted action.
 */
function reviewAction(actionId, isApproved) {
  // ... (This function is unchanged)
  try {
    const sheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(ACTIONS_TAB);
    if (!sheet) throw new Error(`Sheet tab "${ACTIONS_TAB}" not found.`);

    const data = sheet.getDataRange().getValues();
    const headers = data[0];

    const idIndex = findHeaderIndex(headers, "Audit_Row_ID");
    const statusIndex = findHeaderIndex(headers, "Status");
    const updatedIndex = findHeaderIndex(headers, "Last_Updated");

    for (let i = 1; i < data.length; i++) {
      const row = data[i];
      if (row[idIndex] === actionId) {
        const sheetRow = i + 1;
        const newStatus = isApproved ? "Closed" : "Denied";

        sheet.getRange(sheetRow, statusIndex + 1).setValue(newStatus);
        sheet.getRange(sheetRow, updatedIndex + 1).setValue(new Date());

        return { status: "success", message: `Action "${actionId}" has been ${newStatus}.` };
      }
    }

    throw new Error(`Could not find a matching row with ID: ${actionId}`);

  } catch (e) {
    Logger.log(e);
    return { status: "error", message: e.message.toString() };
  }
}

/**
 * A special function that runs when the Google Sheet is opened.
 * This adds a new menu item to your sheet's UI.
 */
function onOpen() {
  const ui = SpreadsheetApp.getUi();
  ui.createMenu('Reporting')
      .addItem('Send Corrective Action Summary (Manual)', 'sendWeeklySummary_MANUAL') // Points to the new manual function
      .addToUi();
}

/**
 * --- NEW: MANUAL FUNCTION ---
 * This function is ONLY for the "Reporting" menu.
 * It calls the main logic and then shows a Browser.msgBox.
 */
function sendWeeklySummary_MANUAL() {
  try {
    const emailList = runWeeklySummary_TRIGGER(); // Run the main logic
    Browser.msgBox("Successfully sent the weekly summary email to: " + emailList);
  } catch (e) {
    Logger.log(e);
    Browser.msgBox("An error occurred: " + e.message);
  }
}

/**
 * --- RENAMED: TRIGGER FUNCTION ---
 * This is the main logic function.
 * It runs silently and is safe for automated triggers.
 * It returns the email list on success so the manual function can use it.
 */
function runWeeklySummary_TRIGGER() {
  try {
    const emailList = getEmailList(); 
    if (!emailList) {
      throw new Error("Could not find any email addresses in the 'Email' tab.");
    }

    const metrics = calculateMetrics(); // Get the numbers

    // Build the email
    const subject = "Weekly Corrective Action Summary";
    const body = `
      Here is your weekly summary of corrective actions:

      - Total Actions Currently "Open": ${metrics.openCount}
      - Total Actions "Pending Review": ${metrics.pendingCount}
      - Actions Due in the Next 7 Days: ${metrics.dueSoonCount}

      Please review the pending actions in the Audit App.
    `;

    const htmlBody = `
      <p>Here is your weekly summary of corrective actions:</p>
      <h2 style="color: #333;">Action Item Status</h2>
      <ul style="font-size: 1.1rem;">
        <li><strong>Total Actions Currently "Open":</strong> ${metrics.openCount}</li>
        <li><strong>Total Actions "Pending Review":</strong> ${metrics.pendingCount}</li>
        <li style="color: #dc3545;"><strong>Actions Due in the Next 7 Days:</strong> ${metrics.dueSoonCount}</li>
      </ul>
      <p>Please use the Audit App's "Review Actions" mode to approve or deny pending items.</p>
    `;

    // Send the email
    GmailApp.sendEmail(emailList, subject, body, {
      htmlBody: htmlBody,
      name: "Automated Audit System"
    });

    Logger.log("Successfully sent the weekly summary email to: " + emailList);
    return emailList; // Return the list for the manual function

  } catch (e) {
    Logger.log(e);
    throw new Error(e.message); // Pass the error up to the manual function if needed
  }
}

/**
 * Calculates the key metrics from the 'Corrective Actions' sheet.
 */
function calculateMetrics() {
  try {
    const sheet = SpreadsheetApp.openById(SHEET_ID).getSheetByName(ACTIONS_TAB); 
    if (!sheet) throw new Error(`Sheet tab "${ACTIONS_TAB}" not found.`);

    const data = sheet.getDataRange().getValues();
    const headers = data[0];

    const statusIndex = findHeaderIndex(headers, "Status");
    const dateIndex = findHeaderIndex(headers, "Target_Date");

    if (statusIndex === -1 || dateIndex === -1) {
      throw new Error("Could not find required 'Status' or 'Target_Date' columns in 'Corrective Actions' sheet.");
    }

    let openCount = 0;
    let pendingCount = 0;
    let dueSoonCount = 0;

    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const oneWeekFromNow = new Date();
    oneWeekFromNow.setDate(today.getDate() + 7);
    oneWeekFromNow.setHours(0, 0, 0, 0);

    for (let i = 1; i < data.length; i++) {
      const row = data[i];
      const status = String(row[statusIndex]).trim().toUpperCase();

      if (status === "OPEN") {
        openCount++;
      }

      if (status === "PENDING REVIEW") {
        pendingCount++;
      }

      if (status !== "CLOSED") {
        const targetDateValue = row[dateIndex];
        if (targetDateValue) {
          try {
            const targetDate = new Date(targetDateValue);
            targetDate.setHours(0, 0, 0, 0);

            if (targetDate >= today && targetDate <= oneWeekFromNow) {
              dueSoonCount++;
            }
          } catch(e) {
            // Ignore invalid dates
          }
        }
      }
    }

    return { openCount, pendingCount, dueSoonCount };

  } catch (e) {
    Logger.log(e);
    throw e;
  }
}
